import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./Menu.css"; // Import CSS for styles

function Meniu({ klase = "", stilius = {}, komandos = [] }) {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [activeIndex, setActiveIndex] = useState(null);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  const handleClick = (index) => {
    setActiveIndex(index);
    closeMenu(); // Close menu after clicking a link
  };

  return (
    <nav className={`menu-container ${klase}`} style={stilius}>
      {/* Hamburger Icon */}
      <div className="hamburger" onClick={toggleMenu}>
        <span className="bar"></span>
        <span className="bar"></span>
        <span className="bar"></span>
      </div>

      {/* Menu Links */}
      <div className={`menu-overlay ${isMenuOpen ? "open" : ""}`}>
        <button className="close-button" onClick={closeMenu}>
          &times;
        </button>
        <div className="menu-links">
          <ul>
            {komandos.map((komanda, index) => (
              <li key={index} className="nav-item">
                <Link
                  to={komanda.adresas}
                  className={`nav-link ${activeIndex === index ? "active-link" : ""}`}
                  onClick={() => handleClick(index)}
                >
                  {komanda.pavadinimas}
                </Link>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </nav>
  );
}

export default Meniu;
