import React, { Component } from "react";
import darbas1 from "./images/darbas1.png";
import darbas2 from "./images/darbas2.jpg";
import darbas3 from "./images/darbas3.png";

class Darbai extends Component {
  constructor(props) {
    super(props);

    this.state = {
      currentIndex: 0, // Tracks the currently displayed large image
      darbai: [
        { src: darbas1, description: "This is Darbas 1, an example of great work." },
        { src: darbas2, description: "This is Darbas 2, showcasing creativity." },
        { src: darbas3, description: "This is Darbas 3, a masterful piece." },
      ],
    };
  }

  handleThumbnailClick = (index) => {
    this.setState({ currentIndex: index });
  };

  handleNext = () => {
    this.setState((prevState) => ({
      currentIndex: (prevState.currentIndex + 1) % prevState.darbai.length,
    }));
  };

  handlePrev = () => {
    this.setState((prevState) => ({
      currentIndex:
        prevState.currentIndex === 0
          ? prevState.darbai.length - 1
          : prevState.currentIndex - 1,
    }));
  };

  render() {
    const { currentIndex, darbai } = this.state;
    const currentDarbas = darbai[currentIndex];

    return (
      <div className="darbai">
        <p>Mano darbai</p>

        {/* Main image display */}
        <div>
          <button onClick={this.handlePrev}>Previous</button>
          <img
            src={currentDarbas.src}
            alt="Darbas"
            width="400"
            style={{ margin: "0 10px" }}
          />
          <button onClick={this.handleNext}>Next</button>
        </div>

        <p>{currentDarbas.description}</p> {/* Display the description */}

        {/* Thumbnail gallery */}
        <div className="thumbnails" style={{ display: "flex", gap: "10px", marginTop: "20px" }}>
          {darbai.map((darbas, index) => (
            <img
              key={index}
              src={darbas.src}
              alt={`Thumbnail ${index + 1}`}
              width="100"
              style={{
                cursor: "pointer",
                border: currentIndex === index ? "3px solid blue" : "2px solid gray",
                borderRadius: "5px",
              }}
              onClick={() => this.handleThumbnailClick(index)}
            />
          ))}
        </div>
      </div>
    );
  }
}

export default Darbai;
