import React, { useState } from "react";
import darbas1 from "./images/darbas1.png";
import darbas2 from "./images/darbas2.jpg";
import darbas3 from "./images/darbas3.png";

function Darbai() {
  // Array of images and descriptions
  const darbai = [
    { src: darbas1, description: "This is Darbas 1, an example of great work." },
    { src: darbas2, description: "This is Darbas 2, showcasing creativity." },
    { src: darbas3, description: "This is Darbas 3, a masterful piece." },
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  // Handlers for navigation
  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % darbai.length);
  };

  const handlePrev = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? darbai.length - 1 : prevIndex - 1
    );
  };

  return (
    <>
      <p>Mano darbai</p>
      <div>
        <button onClick={handlePrev}>Previous</button>
        <img src={darbai[currentIndex].src} alt="Darbas" width="300" />
        <button onClick={handleNext}>Next</button>
      </div>
      <p class="name">{darbai[currentIndex].description}</p> {/* Display the description */}
    </>
  );
}

export default Darbai;
