import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./Meniu.css"; // Create this CSS file for styling

function Meniu({ klase = "", stilius = {}, komandos = [] }) {
  const [activeIndex, setActiveIndex] = useState(null);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const handleClick = (index) => {
    setActiveIndex(index);
    setIsMenuOpen(false); // Close menu after selecting an item
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <nav className="menu">
      <button className="hamburger" onClick={toggleMenu}>
        <i className="fa-solid fa-bars"></i>
      </button>
      <ul className={`nav ${klase} ${isMenuOpen ? "open" : ""}`} style={stilius}>
        {komandos.map((komanda, index) => (
          <li key={index} className="nav-item">
            <Link
              to={komanda.adresas}
              className={`nav-link ${activeIndex === index ? "active-link" : ""}`}
              onClick={() => handleClick(index)}
            >
              {komanda.pavadinimas}
            </Link>
          </li>
        ))}
      </ul>
    </nav>
  );
}

export default Meniu;
